(()=>{"use strict";My$.modal=t=>{const n=function(t){const n=document.createElement("div");return n.classList.add("imodal"),n.insertAdjacentHTML("afterbegin",`\n     <div class="modal-overlay">\n        <div class="modal-window">\n            ${t.content||""}\n            <div class="modal-buttons">\n                <button type="button" class="btn btn-danger" id="yes">Yes</button>\n                <button type="button" class="btn btn-outline-dark" id="no">No!</button>\n            </div>\n        </div>\n     </div> \n    `),document.body.appendChild(n),n}(t);let e=!1;return{open(){e||n.classList.add("open")},close(){e=!0,n.classList.remove("open"),n.classList.add("hide"),setTimeout((()=>{n.classList.remove("hide"),e=!1}),200)}}}})();